INSERT INTO public.questions (id, theme_id, title, answers, correct_answer) VALUES (205, 5, '1. Первичное наблюдение осуществляется с целью', '{а) контроля и анализа хозяйственной деятельности;,б) производства записей в системе счетов бухгалтерского учета;,в) последующей обработки наблюдаемых фактов в информационные пока-затели;,г) составления первичных документов.}', 'в');
