INSERT INTO public.questions (id, theme_id, title, answers, correct_answer) VALUES (317, 7, '52. В соответствии с Концепцией бухгалтерского учета на среднесрочную перспективу профессиональные сообщества регулируют', '{а) подготовку предложений по совершенствованию правовых основ бухгал-терского учета, отчетности и аудита;,б) экспертизу МСФО и процесс их одобрения в Российской Федерации;,в) контроль за соблюдением законодательства по бухгалтерскому учету, отчетности и аудиту;,г) установление процедуры одобрения МСФО и введения их в действие;,д) разработку норм профессиональной этики и контроль за их соблюде-нием;,е) повышение квалификации членов профессионального сообщества.}', 'абде');
