INSERT INTO public.questions (id, theme_id, title, answers, correct_answer) VALUES (391, 9, '37. Оприходование готовой продукции по нормативной себестоимости отра-жается записью', '{а) Д-т сч. 20 «Основное производство» — К-т сч. 43 «Готовая продукция»;,б) Д-т сч. 40 «Выпуск продукции (работ, услуг)» — К-т сч. 20 «Основное производство»;,в) Д-т сч. 43 «Готовая продукция» — К-т сч. 40 «Выпуск продукции (работ, услуг)».}', 'в');
INSERT INTO public.questions (id, theme_id, title, answers, correct_answer) VALUES (392, 9, '38. Списание фактической производственной себестоимости выпущенной про-дукции отражается записью', '{а) Д-т сч. 40 «Выпуск продукции (работ, услуг)» — К-т сч. 20 «Основное про-изводство»;,б) Д-т сч. 20 «Основное производство» — К-т сч. 40 «Выпуск продукции (работ, услуг)»;,в) Д-т сч. 43 «Готовая продукция» — К-т сч. 40 «Выпуск продукции (работ, услуг)».}', 'а');
