INSERT INTO public.questions (id, theme_id, title, answers, correct_answer) VALUES (394, 9, '40. Продукция считается отгруженной, если она', '{а) отправлена покупателю;,б) отправлена покупателю, а документы сданы в банк, но не оплачены;,в) отправлена покупателю, а документы сданы в банк и оплачены.}', 'б');
