INSERT INTO public.questions (id, theme_id, title, answers, correct_answer) VALUES (396, 9, '42. Списание расходов на продажу продукции отражается записью', '{а) Д-т сч. 44 «Расходы на продажу» — К-т сч. 90 «Продажи»;,б) Д-т сч. 90 «Продажи» — К-т сч. 44 «Расходы на продажу»;,в) Д-тсч.44«Расходынапродажу»—К-тсч.71«Расчетысподотчетнымили-цами».}', 'б');
INSERT INTO public.questions (id, theme_id, title, answers, correct_answer) VALUES (397, 9, '43. Запись «Д-т сч. 90 "Продажи" — К-т сч. 68 "Расчеты по налогам и сбо-рам"» означает', '{а) начисление налога на добавленную стоимость по проданной продукции;,б) получение сумм налога на добавленную стоимость от покупателя;,в) зачет сумм налога на добавленную стоимость.}', 'а');
INSERT INTO public.questions (id, theme_id, title, answers, correct_answer) VALUES (398, 9, '44. Запись «Д-т сч. 62 "Расчеты с покупателями и заказчиками" — К-т сч. 90 "Продажи"» означает', '{а) оплату продукции;,б) отгрузку продукции;,в) долг покупателей за поставленную продукцию.}', 'в');
INSERT INTO public.questions (id, theme_id, title, answers, correct_answer) VALUES (399, 9, '45. Запись «Д-т сч. 90 "Продажи" — К-т сч. 43 "Готовая продукция"» озна-чает', '{а) списание себестоимости отгруженной продукции;,б) оплату продукции;,в) долг покупателя за продукцию.}', 'а');
INSERT INTO public.questions (id, theme_id, title, answers, correct_answer) VALUES (400, 9, '46. Запись «Д-т сч. 90 "Продажи" — К-т сч. 99 "Прибыли и убытки"» означа-ет списание', '{а) расходов на продажу;,б) убытка от продажи;,в) прибыли от продажи.}', 'в');
