INSERT INTO public.questions (id, theme_id, title, answers, correct_answer) VALUES (405, 9, '51. Прибыль от продажи продукции, работ и услуг отражается записью по кредиту сч. 99 «Прибыли и убытки» и дебету счета 90 «Продажи» по субсчету', '{а) 1 «Выручка»;,б) 2 «Себестоимость продаж»;,в) 3 НДС»;,г) 9 «Прибыль / убыток от продаж».}', 'г');
INSERT INTO public.questions (id, theme_id, title, answers, correct_answer) VALUES (406, 9, '52. По окончании финансового года сч. 90 «Продажи», субсчет 9 «Прибыль / убыток от продаж», закрывается посредством', '{а) списания финансового результата от продажи продукции на сч. 99 «При-были и убытки»;,б) осуществления внутренних записей по субсчетам сч. 90 «Продажи»;,в) записей на сч. 91 «Прочие доходы и расходы».}', 'б');
