INSERT INTO public.questions (id, theme_id, title, answers, correct_answer) VALUES (592, 12, '28. При поступлении средств в оплату акций от учредителей (участни-ков) делается запись по дебету счетов учета денежных средств и ценностей и кредиту счета', '{а) 80 «Уставный капитал», субсчет «Объявленный капитал»;,б) 80 «Уставный капитал», субсчет «Подписной капитал»;,в) 80 «Уставный капитал», субсчет «Оплаченный капитал»;,г) 75 «Расчеты с учредителями».}', 'г');
