INSERT INTO public.questions (id, theme_id, title, answers, correct_answer) VALUES (725, 17, '13. При несопоставимости данных отчетного периода с данными предшест-вующего осуществляется корректировка информации', '{а) отчетного периода;,б) предшествующего периода.}', 'б');
