INSERT INTO public.questions (id, theme_id, title, answers, correct_answer) VALUES (752, 18, '4. Материально-производственные запасы принимаются к учету по себе-стоимости', '{а) учетной;,б) фактической;,в) нормативной;,г) средней.}', 'б');
