INSERT INTO public.questions (id, theme_id, title, answers, correct_answer) VALUES (871, 23, '22. При раскрытии информации по отчетным сегментам выделяется инфор-мация', '{а) сводная;,б) внутренняя;,в) первичная;,г) вторичная.}', 'вг');
